import React, { } from 'react';
import './login.css';
import { NavLink, } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import './mylogin.css';
import Avatar from '@material-ui/core/Avatar';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { isEmail, isEmpty } from './validator';



let code_to_Verify = "";
let code = '';

class Verify extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            number: '',
            code: '',
            formData: {},
            errors: {},
            otpVerifyResponse: []

        };
        this.handleCode = this.handleCode.bind(this);
        this.handleC = this.handleC.bind(this);
    }
    

    handleC(event) {
        this.setState({ code: event.target.value });
        code = this.state.code;
    }


    sendOtp(number) {
        fetch('https://cors-anywhere.herokuapp.com/http://34.76.147.17:8080/otp/send', {
            method: 'POST',
            body: JSON.stringify({
                phoneNumber: number,
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => {
            return response.json()
        }).then(json => {
            this.setState({
                otpSendResponse: json
            });
        });
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        let { formData } = this.state;
        formData[name] = value;

        this.setState({
            formData: formData
        });
    }


    verifyOtp(number, c) {
        fetch('http://localhost:8080/otp/verify', {
            method: 'POST',
            body: JSON.stringify({
                phoneNumber: number,
                code: c,
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => {
            return response.json()
        }).then(json => {
            this.setState({
                otpVerifyResponse: json
            });
        });

    }

    validateLoginForm = (e) => {
       
        let errors = {};
        const { formData } = this.state;

        if (isEmpty(formData.code)) {
            errors.code = "Code Can't be Blank";
            return false;
        } 
        else
        {
            this.verifyOtp(formData.code);
            setTimeout(()=>{
            if(this.state.otpVerifyResponse.errorMessage !== null){
                errors.password = "Not an active OTP Code";
                return false
            }
            else{
                if(this.state.otpVerifyResponse.valid === 'true')
                    return true;
                else
                    return false
            }
            },1000);
        }
        return false;
    }

    handleCode(e) {
       // this.verifyOtp(this.state.number, this.state.code);

        e.preventDefault();
        let errors = this.validateLoginForm();

        if (errors === true) {
            this.verifyOtp(this.state.number, this.state.code)

        } else {

            this.setState({

                errors: errors,
                formSubmitted: true
            });

        }



    }

    render() {
        const { errors } = this.state;
    
        return (
           <div>
                <div>
                    <div id="limiter">
                        <div id="container-login100">
                            <div id-="wrap-login100">
                                <div id="lo">
                                    <Avatar id="avatar">
                                        <LockOutlinedIcon />
                                    </Avatar>
                                    <div id="log">
                                        OTP
                                </div>
                                </div>
                                <form id="lo" onSubmit={this.handleCode}>

                                    <div id="wrap-input100 validate-input "
                                        data-validate="username is required">

                                        <TextField type="text" name="code" placeholder="Verification code" onChange={this.handleInputChange} />
                                        {errors.code &&
                                            <div id="errors">{errors.code}</div>
                                        }

                                    </div>





                                    <div id="container-login100-form-btn">
                                        <button id="login100-form-btn" >
                                            Login
                                </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div >
                </div>
                <NavLink to="/partial">click here to go to partial password</NavLink>

            </div>


           
        )
    }

}

export default Verify
