import React from 'react';
import './login.css';
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";


var code_to_Verify = "no number entered yet";

var x, y, z, a, b, c;
var password = '351235';
var secret = '';
var array = ['first', 'second', 'third', 'fourth', 'fifth', 'sixth', 'seventh', 'eighth', 'ninth', 'tenth'];
let count = 0;


class Verify extends React.Component {

    // Using a class based component here because we're accessing DOM refs
    constructor(props) {
        super(props);
        this.state = {
            number: '',
            formData:{}
        };
        this.handleNumber = this.handleNumber.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleNumber(event) {
        this.setState({ number: event.target.value });
    }

    handleSubmit(event) {
        count = 0;
        code_to_Verify = this.state.number;
        //  this.setState({ number: '' });
        this.check();
        event.preventDefault();
        secret = '';
    }

      handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        let { formData } = this.state;
        formData[name] = value;

        this.setState({
            formData: formData
        });
    }




    check(event) {
        var copy_password = "";
        for (let i = 0; i < password.length; i++) {
            if (secret.charAt(i) === '*') {
                if (count === 0) {
                    copy_password += x;
                }
                else if (count === 1) {
                    copy_password += y;
                }
                else if (count === 2) {
                    copy_password += z;
                }
                count++;
            }
            else {
                copy_password += password.charAt(i);
            }
        }

        if (copy_password === password) {
            alert('Password Success');
            //window.location.reload();
            //window.location.href = '/Secure'
            //  return <Redirect to="/Secure" />;
            this.props.history.push('/')
        }
        else {
            alert('Password Fail');
            //this.props.history.pop();

            window.location.reload();
        }
    }

    handleX(event) {
        x = event.target.value;
        // event.target.value = "*";


    }
    handleY(event) {
        y = event.target.value;
        // event.target.value = "*";

    }
    handleZ(event) {
        z = event.target.value;
        // event.target.value = "*";
    }


    render() {
        const{formData}=this.state;
        console.log(formData.index1);
        return (
            < body >
                <div class="limiter">
                    <div class="container-login100">
                        <div class-="wrap-login100">
                            <form className="login100-form validate-form" onSubmit={this.handleSubmit}>
                                <div class="wrap-input100">
                                   Enter Index {localStorage.getItem('index1')} of Your Password:
         <p> <TextField  type="text" controlid="index1" number={''} placeholder="enter the number" onChange={this.handleInputChange} /></p>
                                </div>
                                <div class="wrap-input100" >
                                    Enter Index {localStorage.getItem('index2')} of Your Password:
         <p> <TextField  type="text" controlid="index2" number={''} placeholder="enter the number" onChange={this.handleInputChange} /></p>
                                </div>
                                <div class="wrap-input100" >
                                    Enter Index {localStorage.getItem('index3')} of Your Password:
         <p> <TextField  type="text" controlid="index3" number={''} placeholder="enter the number" onChange={this.handleInputChange} /></p>

                                </div>
                                <div class="container-login100-form-btn">
                                    <div class="container-login100-form-btn">
                                        <button class="login100-form-btn" >
                                            Login
                                </button>
                                    </div>
                                </div>

                            </form>

                        </div>
                    </div>
                </div>
            </ body>
        )

    }

}

export default Verify
