import React from 'react';
import './login.css';
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";


var code_to_Verify = "no number entered yet";

var x, y, z;
class Verify extends React.Component {

    // Using a class based component here because we're accessing DOM refs
    constructor(props) {
        super(props);
        this.state = {
            number: '',
           
        };
    }

    handleNumber(event) {
        this.setState({ number: event.target.value });
    }

    handleSubmit(event) {
       console.log(x)
       console.log(y)
       console.log(z)
       var fullIndexs = " ";
       var fullPartial = " ";
       console.log(fullIndexs.concat(localStorage.getItem('index1'), localStorage.getItem('index2'), localStorage.getItem('index3')));
       console.log(fullPartial.concat(x,y,z))
    }

    verifyPartialPassword(username) {
        var fullIndexs = " ";
        var fullPartial = " ";
        fetch('http://localhost:8080/validate//verifyPartialPassword', {
            method: 'POST',
            body: JSON.stringify({
                userName: username,
                partialPassword: fullPartial.concat(x,y,z),
                indexes: fullIndexs.concat(localStorage.getItem('index1'), localStorage.getItem('index2'), localStorage.getItem('index3'))
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => {
            return response.json()
        }).then(json => {
            this.setState({
                partialPasswordGenerateResponse: json
            });
        });

    }

    handleX(event) {
        x = event.target.value;
    }

    handleY(event) {
        y = event.target.value;
    }

    handleZ(event) {
        z = event.target.value;
    }


    render() {
        
        return (
            < body >
                <div class="limiter">
                    <div class="container-login100">
                        <div class-="wrap-login100">
                            <form className="login100-form validate-form" onSubmit={this.handleSubmit}>
                                <div class="wrap-input100"ontrolId="index1" >
                                   Enter Index {localStorage.getItem('index1')} of Your Password:
         <p> <TextField  type="text" cnumber={''} placeholder="enter the number" onChange={this.handleX} /></p>
                                </div>
                                <div class="wrap-input100"controlId="index2" >
                                    Enter Index {localStorage.getItem('index2')} of Your Password:
         <p> <TextField  type="text"  number={''} placeholder="enter the number" onChange={this.handleY} /></p>
                                </div>
                                <div class="wrap-input100"controlId="index3"  >
                                    Enter Index {localStorage.getItem('index3')} of Your Password:
         <p> <TextField  type="text" number={''} placeholder="enter the number" onChange={this.handleZ} /></p>

                                </div>
                                <div class="container-login100-form-btn">
                                    <div class="container-login100-form-btn">
                                        <button class="login100-form-btn" >
                                            Login
                                </button>
                                    </div>
                                </div>

                            </form>

                        </div>
                    </div>
                </div>
            </ body>
        )

    }

}

export default Verify
